import React from "react";

import githubMark from "../../images/github-mark.svg";
import linkedIn from "../../images/linkedin.png";

import styles from "./Header.module.css";

const Header = ({ setActiveSection, activeSection }) => {
  let experienceBtnClass = "";
  let projectsBtnClass = "";
  let certificationsBtnClass = "";
  let technicalSkillsBtnClass = "";
  let blogBtnClass = "";

  const changeActiveButton = () => {
    switch (activeSection) {
      case "experience":
        experienceBtnClass = styles.active;
        break;
      case "projects":
        projectsBtnClass = styles.active;
        break;
      case "certifications":
        certificationsBtnClass = styles.active;
        break;
      case "technical_skills":
        technicalSkillsBtnClass = styles.active;
        break;
      case "blog":
        blogBtnClass = styles.active;
        break;
      default:
        break;
    }
  };

  const handleButtonClick = (event) => {
    setActiveSection(event.target.value);
    changeActiveButton();
  };

  return (
    <header className={styles.container}>
      <h1 className={styles.header_title}>Jabez Dailey</h1>
      <h2 className={styles.header_text}>Aspiring Software Engineer</h2>
      <p>
        I am a Senior Computer Science Student at Morehouse College passionate
        about software development, artificial intelligence, and web
        technologies. Welcome to my personal website!
      </p>
      <div className={styles.links}>
        <button
          onClick={handleButtonClick}
          value={"experience"}
          className={experienceBtnClass}
        >
          Experience
        </button>
        <button
          onClick={handleButtonClick}
          className={projectsBtnClass}
          value="projects"
          type="button"
        >
          Projects
        </button>
        <button
          onClick={handleButtonClick}
          className={certificationsBtnClass}
          value="certifications"
          type="button"
        >
          Certifications
        </button>
        <button
          onClick={handleButtonClick}
          className={technicalSkillsBtnClass}
          value="technical_skills"
          type="button"
        >
          Technical Skills
        </button>
        <button
          onClick={handleButtonClick}
          className={blogBtnClass}
          value="blog"
          type="button"
        >
          Blog
        </button>
      </div>
      <div className={styles.icons}>
        <a
          href="https://github.com/BezDailey"
          target="_blank"
          rel="noopener noreferrer"
        >
          <img className={styles.icon} src={githubMark} alt="Github Icon" />
        </a>
        <a
          href="https://www.linkedin.com/in/jabez-dailey-a13b71193/"
          target="_blank"
          rel="noopener noreferrer"
        >
          <img className={styles.icon} src={linkedIn} alt="LinkedIn Icon" />
        </a>
      </div>
    </header>
  );
};

export default Header;
