import React, {useState} from "react";

import styles from "./TemplateBlog.module.css";
import Skill from "../../Skill/Skill"

const TemplateBlog = ({header, short_text, topic, date_posted}) => {
  const [isBlogOpen, setIsBlogOpen] = useState(false);

  const openBlog = () => {
    setIsBlogOpen(true);
  }

  const closeModal = () => {
    setIsBlogOpen(false);
  }

  if (isModalOpen) {
    return (
      <blog class={styles.modalOverlay}>
        <button onClick={closeModal}>X</button>
        <p>Blog</p>
      </blog>
    )
  }

  return (
    <div class={styles.templateBlog}>
      <div class={styles.topHalf}>
        <h1>{header}</h1>
        <p class={styles.shortText}>{short_text}</p>
      </div>
      <button onClick={openModal} class={styles.templateBlogBtn}>Read blog {'->'}</button>
      <div class={styles.blogInfo}>
        <div>
          <Skill name={topic} />
        </div>
        <p>{date_posted}</p>
      </div>
    </div>
  )
}

export default TemplateBlog
